												$filtros="WHERE `anu_color` LIKE '%$color%' AND `anu_ubicacio_robatori`LIKE '%$ubicacion%' AND `anu_marca` LIKE '%$marca%' AND `anu_model` LIKE '%$modelo%' AND `anu_data_robatori` LIKE '%$fechaRobo%' AND `anu_compensacio` LIKE '%$compensacion%'";
