<script type="text/javascript">
	var data = "";
	var active = false;
	jQuery(window).load(function() {
		if(jQuery('body').find('#resultLoading').attr('id') != 'resultLoading'){
			jQuery('body').append('<div id="resultLoading" style="display:none"><div><p><img src="media/magehouse/slider/default/ajax-loader.gif"></p><div>Fetching Results... Please wait...</div></div><div class="bg"></div></div>');
		}
		var height = jQuery('body').outerHeight();
		var width = jQuery('body').outerWidth();
		jQuery('.ui-slider-handle').css('cursor','pointer');

		jQuery('#resultLoading').css({
			'width':width,
			'height':height+'px',
			'position':'absolute',
			'z-index':'10000000',
			'top':'0',
			'left':'0'
		});	
		jQuery('#resultLoading .bg').css({
			'background':'#000000',
			'opacity':'0.3',
			'width':'100%',
			'height':'100%',
			'position':'absolute',
			'top':'0'
		});


		function loadInd(){
			loadIndent = jQuery(window).height()/2+'px';
			jQuery('#resultLoading>div:first').css('margin-top', loadIndent);
		}
		loadInd();
		jQuery(window).resize(function(){loadInd();});

		jQuery('#resultLoading>div:first').css({
			'width': '100%',
			'text-align': 'center',
			'position': 'absolute',
			'left': '0',
			'font-size':'16px',
			'z-index':'10',
			'color':'#000000'

		});

		jQuery('.block-layered-nav a').off('click.vs');
		jQuery(document).on("click", ".block-layered-nav a", function (event){
			if(!jQuery(this).parents('#narrow-by-list2').length){
				event.preventDefault();
				var url = jQuery(this).attr("href");
				sliderAjax(url);
			}
		});
	});

	/*DONOT EDIT THIS CODE*/
	function sliderAjax(url) {
		if (!active) {
			active = true;
			jQuery(function($) {
				oldUrl = url;
				$('#resultLoading .bg').height('100%');
				$('#resultLoading').fadeIn(300);
				try {
					$('body').css('cursor', 'wait');
					$.ajax({
						url: url,
						dataType: 'json',
						type: 'post',
						data: data,
						success: function(data) {
							callback();
							if (data.viewpanel) {
								if ($('.block-layered-nav')) {
									$('.block-layered-nav').after('<div class="ajax-replace" />').remove();
									$('.ajax-replace').after(data.viewpanel).remove();
								}
							}
							if (data.productlist) {
								$('.category-products').after('<div class="ajax-category-replace" />').remove();
								$('.ajax-category-replace').after(data.productlist).remove();
							}
							var hist = url.split('?');
							if(window.history && window.history.pushState){
								window.history.pushState('GET', data.title, url);
							}
							$('body').find('.toolbar select').removeAttr('onchange');
							$('#resultLoading .bg').height('100%');
							$('#resultLoading').fadeOut(300);
							$('body').css('cursor', 'default');
							ajaxtoolbar.onReady();
							jQuery('.block-layered-nav a').off('click.vs');
							try{
								ConfigurableSwatchesList.init();
							}catch(err){}
						}
					})
				} catch (e) {}
			});
			active = false
		}
		return false
	}

	function callback(){
	}
</script>
